mapper.select:
SELECT

FROM alert_data

    
        AND iot_id = #{iotId}
    
    
        AND device_name LIKE CONCAT('%', #{deviceName}, '%')
    
    
        AND alert_rule_id = #{alertRuleId}
    
    
        AND type = #{type}
    
    
        AND status = #{status}
    
    
        AND create_time >= #{startTime}
    
    
        AND create_time <= #{endTime}
    

ORDER BY id DESC

SELECT

FROM alert_data
WHERE iot_id = #{iotId,jdbcType=VARCHAR}

    AND create_time >= #{startTime,jdbcType=TIMESTAMP}


    AND create_time <= #{endTime,jdbcType=TIMESTAMP}

ORDER BY create_time DESC

mapper.delete:
DELETE FROM alert_data
WHERE id IN

    #{id}
