mapper.update:
update reservation
name = #{name}, mobile = #{mobile}, time = #{time}, visitor = #{visitor,jdbcType=VARCHAR}, type = #{type}, status = #{status}, update_by = #{updateBy}, remark = #{remark}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select * from reservation

    
        and name like concat('%', #{name}, '%')
    
    
        and mobile = #{phone}
    
    
        and status = #{status}
    
    
        and type = #{type}
    
    
        and time between #{startTime} and #{endTime}
    
    
        and time >= #{startTime}
    
    
        and time <= #{endTime}
    

order by create_time desc
